package Numbers;////183. Implement a program in Java to find the sum of all prime numbers within a given range (1--50).class SumOf1to50Prime {    static boolean isPrime(int n) {        if (n <= 1) {            return false;        }        for (int i = 2; i < n; i++) {            if (n % i == 0) {                return false;            }        }        return true;    }    private  static void main(String[] args) {        int sum = 0;        int start = 1;        int end = 50;        for (int n = start; n <= end; n++) {            //  System.out.println(n+"  is a prime ?  : "+ isPrime(n));            if (isPrime(n)) {                sum = sum + n;            }        }        System.out.println("Total sum of prime number from " + start + "  to " + end + " is : " + sum);    }}